-- ----------------------------
-- Table structure for `be_article_comment`
-- ----------------------------
DROP TABLE IF EXISTS `be_article_comment`;
CREATE TABLE `be_article_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `user_name` varchar(120) NOT NULL,
  `body` text NOT NULL,
  `ip` varchar(15) NOT NULL,
  `like` int(11) NOT NULL,
  `dislike` int(11) NOT NULL,
  `create_time` int(11) NOT NULL,
  `block` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=22 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of be_article_comment
-- ----------------------------
INSERT INTO `be_article_comment` VALUES ('13', '7', '1', '管理员', '没有具体了解以前不发表评论。线下和线上的整合分离是相统一的。@电商老兵斗牛士 举了个不恰当例子。咖啡等快销品本就不会是这种貌似分离实际整合的线上到线下聚汇网点的主营产品甚至主营业务。这可以看作是物流节点的单纯铺展，但比节点来说，一方面，的确有新瓶装旧酒之嫌。另一方面，也有让顾客冲动型消费的可能。总的来说，他们此举不是在开便利店，而是以培养顾客惯性与建立最后一公里服务点为目的的物流网点而已。', '127.0.0.1', '3', '0', '1395217096', '0');
INSERT INTO `be_article_comment` VALUES ('14', '7', '1', '管理员', '开个便利店不容易，如何吸引周边用户，还是很重要的，便利店可以开辟带客户收快递的业务，至于大半夜给人家送点冰激凌、啤酒什么的，还是交给楼下下卖铺就行了', '127.0.0.1', '4', '1', '1395217121', '0');
INSERT INTO `be_article_comment` VALUES ('15', '11', '1', '管理员', '京东只是为了自己发展，根本不会顾及线下的死活，举例：这样一整绝对是排挤其他的没合作的，其他没合作了肯定降低价格，这样又排挤合作了，好吧一个恶性循环。。。发展需要支持信任，应该学习团购网，主动吸引他们来驻扎，吸引靠的就是提高零售店利润，而且不应排挤那些个体的不是连锁的零售店，京东这样根本是态度和出发点就跑偏了，互联网以后就是服务于线下的生存，摆正自己位置。。。。拭目以待吧。。', '127.0.0.1', '0', '0', '1395232137', '0');
INSERT INTO `be_article_comment` VALUES ('17', '11', '1', '管理员', '&lt;\r\n\r\n\r\n\r\n\r\n#虎嗅网评论#：开个便利店不容易，如何吸引周边用户，还是很重要的，便利店可以开辟带客户收快递的业务，至于大半夜给人家送点冰激凌、啤酒什么的，还是交给楼下下卖铺就行了...【以京东为例，说说巨头O2O的“反人类”】…@虎嗅网\r\n\r\n\r\n#虎嗅网评论#：开个便利店不容易，如何吸引周边用户，还是很重要的，便利店可以开辟带客户收快递的业务，至于大半夜给人家送点冰激凌、啤酒什么的，还是交给楼下下卖铺就行了...【以京东为例，说说巨头O2O的“反人类”】…@虎嗅网', '127.0.0.1', '1', '1', '1395232201', '0');
INSERT INTO `be_article_comment` VALUES ('18', '11', '1', '管理员', '观点的倾向太明显，很容易让人以为是枪文。对阿里或者京东，谁家的O2O，都一样有亮点，有遗憾。我的同事有在淘点点订餐等候两个多小时的经历。但我对新事物的态度是宽容，现在体验差不等于一直体验差，阿里和京东都在探索前行，包括我们在内。话说回来，老兵的观点同样很有亮点，也希望京东能在相关方面做出改进。毕竟，有人做雷锋，免费帮你们寻找工作中的问题，不是吗？', '127.0.0.1', '2', '0', '1395232284', '0');
INSERT INTO `be_article_comment` VALUES ('21', '7', '2', '刘大力1', '测试测试测试测试测试', '127.0.0.1', '1', '0', '1396246648', '0');
INSERT INTO `be_article_comment` VALUES ('20', '10', '1', '管理员', '　　今年所预期的市场波动性很可能是多空策略对冲基金表现的理想条件。不要忘记固定收益投资在使另类投资复兴的过程中发挥的重要作用。随着利息上涨和债券败，固定收益对冲基金被视为投资组合的一项替代保险。考虑到固定收益配置比例已从2012年的34%降至如今的26%，这也许是明智的建议。这就是另类投资的意义所在，即如果购买方向正确会进一步分散投资组合的固有风险。(柠楠/编译)', '127.0.0.1', '1', '0', '1395311540', '0');

